import { motion } from "framer-motion";

export default function Hero() {
  return (
    <section className="relative flex items-center justify-center min-h-screen bg-gradient-to-r from-indigo-50 via-white to-indigo-100 overflow-hidden">

      {/* Floating Background Shape */}
      <motion.div
        className="absolute top-20 right-20 w-40 h-40 bg-indigo-300 rounded-full opacity-30 blur-2xl"
        animate={{ y: [0, 30, 0], x: [0, -20, 0] }}
        transition={{ repeat: Infinity, duration: 6, ease: "easeInOut" }}
      />

      {/* Hero Content */}
      <div className="max-w-4xl text-center px-6 z-10">

        {/* Headline */}
        <motion.h1
          className="text-5xl md:text-6xl font-extrabold text-gray-900"
          initial={{ opacity: 0, y: 40 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 1 }}
        >
          Crafting <span className="text-indigo-600">Digital Experiences</span>
        </motion.h1>

        {/* Subtext */}
        <motion.p
          className="mt-6 text-lg md:text-xl text-gray-600"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ delay: 0.5, duration: 1 }}
        >
          We design and build stunning websites, apps, and digital solutions that leave a mark.
        </motion.p>

        {/* Call To Action Button */}
        <motion.button
          whileHover={{ scale: 1.1 }}
          whileTap={{ scale: 0.9 }}
          className="mt-8 px-8 py-3 bg-indigo-600 text-white text-lg font-medium rounded-xl shadow-lg hover:bg-indigo-700 transition"
        >
          Get Started
        </motion.button>
      </div>
    </section>
  );
}
