import { useState } from "react";
import { motion } from "framer-motion";

export default function Navbar() {
  const [open, setOpen] = useState(false);

  const menuItems = ["Home", "Services", "Work", "About", "Contact"];

  return (
    <nav className="fixed w-full top-0 left-0 z-50 bg-white/70 backdrop-blur-md shadow-sm">
      <div className="max-w-7xl mx-auto px-6 py-4 flex justify-between items-center">
        <motion.h1
          initial={{ opacity: 0, y: -20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6 }}
          className="text-2xl font-bold text-gray-900"
        >
          Leo9<span className="text-indigo-600">Clone</span>
        </motion.h1>

        <ul className="hidden md:flex gap-8 text-gray-700 font-medium">
          {menuItems.map((item, i) => (
            <motion.li
              key={i}
              whileHover={{ scale: 1.1 }}
              className="cursor-pointer hover:text-indigo-600 transition"
            >
              {item}
            </motion.li>
          ))}
        </ul>

        <div className="md:hidden">
          <button
            onClick={() => setOpen(!open)}
            className="text-gray-800 focus:outline-none"
          >
            â˜°
          </button>
        </div>
      </div>

      {open && (
        <motion.ul
          initial={{ opacity: 0, y: -20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.3 }}
          className="md:hidden flex flex-col bg-white shadow-md px-6 py-4 space-y-3"
        >
          {menuItems.map((item, i) => (
            <li
              key={i}
              className="cursor-pointer hover:text-indigo-600 transition"
            >
              {item}
            </li>
          ))}
        </motion.ul>
      )}
    </nav>
  );
}
